const updateSearch = (newText: string) => {
    searchText.value = newText;
}

const updatedArticles = computed(() => {
    if (!searchText.value)
        return articles;

    const matches: Article[] = [];
    articles.map((article) => {
        if (article.title.toLowerCase().includes(searchText.value.trim().toLowerCase()) || article.content.toLowerCase().includes(searchText.value.trim().toLowerCase())) {
            matches.push(
                {
                    title: article.title.replace(
                        new RegExp(searchText.value, "gi"),
                        "<mark>$&</mark>"
                    ),
                    content: article.content.replace(
                        new RegExp(searchText.value, "gi"),
                        "<mark>$&</mark>"
                    ),
                }
            );
        }
    });
    return matches;
});

