    </Head>
    <div class="w-full lg:w-8/12">
        <h1 class="text-3xl font-bold mb-12"> Text Search</h1>
        <SearchInput :searchText="searchText" @updateSearchText="updateSearch" />
        <p class="mt-6" v-if="searchText"><strong> {{ updatedArticles.length }} articles </strong> were found.
        </p>
        <section class="flex flex-col mt-12">
            <div v-for="({ title, content }, index)  in updatedArticles" :key="index">
                <Article :title="title" :content="content" />
                <hr v-if="updatedArticles.length != index + 1"
                    class="my-8 h-[1px] border-0 bg-slate-300 min-w-[300px] w-10/12 mx-auto" />
            </div>
        </section>
    </div>
</template>